import React, { useState } from 'react';
import './jobSearch.css';
import Modal from 'react-bootstrap/Modal';

const jobList = [
  {
    title: 'Quality Assurance',
    company: 'DW Morgan',
    info: 'Summary : The Quality Assurance intern will focus on tetsing web and mobile applications.This involves working closely with the entire ',
    location: 'Zuellig Building, Paseo de Roxas, Makati, Metro Manila',
  },
  {
    title: 'Platform Support Technician',
    company: 'Paragon Outsourcing',
    location: 'Paragon Building, 2/F, McArthur Highway, Clark Freeport Area, Philippines 2009',
  },
  {
    title: 'Front-end Web Developers',
    company: 'Webcast Technologies',
    location: 'Quezon City',
  },
];

function JobSearch() {
  const [query, setQuery] = useState('');
  const [filteredJobs, setFilteredJobs] = useState(jobList);
  const [selectedJob, setSelectedJob] = useState(null);

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    const filtered = jobList.filter(job =>
      job.title.toLowerCase().includes(query.toLowerCase())
      || job.company.toLowerCase().includes(query.toLowerCase())
      || job.location.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredJobs(filtered);
  };

  const handleApplyClick = (job) => {
    setSelectedJob(job);
  };

  const handleCloseModal = () => {
    setSelectedJob(null);
  };

  return (
    <div className="jobcontainer">
      <header className="header">
        <h1 className = "jobTitle">FullJob Search</h1>
        <form className="search-form" onSubmit={handleSearchSubmit}>
          <input type="text" placeholder="Search for a job..." value={query} onChange={(e) => setQuery(e.target.value)} />
          <button type="submit">Search</button>
        </form>
      </header>
      <div className="jobs-list">
        {filteredJobs.map(job => (
          <div className="job" key={job.title}>
            <h2>{job.title}</h2>
            <p>{job.company}</p>
            <p>{job.location}</p>
            <button onClick={() => handleApplyClick(job)}>Apply Now</button>
          </div>
        ))}
      </div>
      <Modal show={selectedJob !== null} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedJob && selectedJob.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>{selectedJob && selectedJob.company}</p>
          <p>{selectedJob && selectedJob.location}</p>
          <p>{selectedJob && selectedJob.info}</p>
          <form>
            <label htmlFor="name">Your Name:</label>
            <br/>
            <input type="text" id="name" name="name" required />
            <br/>
            <label htmlFor="email">Your Email:</label>
            <br/>
            <input type="email" id="email" name="email" required />
            <button type="submit">Submit Application</button>
          </form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default JobSearch;
